import * as React from 'react';
import {Formik,Form} from "formik";
import * as Yup from "yup";
import FormikControl from '../FormComponents/FormikControl';

const initialValues={
    firstName:"",
    lastName:"",
    emailId:"",
    phoneNo:"",
}
const validationSchema=Yup.object({
    firstName:Yup.string().required("First Name is required"),
    lastName:Yup.string().required("Second Name is required"),
    emailId:Yup.string().email("Invalid Email Format").required("Email Id is Required"),
    phoneNo:Yup.number().typeError("Enter a Valid Phone Number").required("Phone Number is Required"),
})
const onSubmit=(values,onSubmitProps)=>{
    console.log("Form values",values)
}
function ResidentDetailsForm() {
    const [formStep,setFormStep]=React.useState(0);

    const WingOptions=[
        {key:"select a Wing", value:""},
        {key:"A",value:"A"},
        {key:"A",value:"A"},
        {key:"A",value:"A"},
        {key:"A",value:"A"},
        {key:"A",value:"A"}
    ]
    const dropdownOptions=[
       
        {key:"101", value:"101"},
        {key:"102", value:"102"},
        {key:"103", value:"103"},
        {key:"104", value:"104"},
        {key:"105", value:"105"},
    ]
    const RenderNextButton=()=>{
        if (formStep===3)
            return <FormikControl
                    control="button"
                    variant="primary"
                    type="submit"
                    children="Submit"
                    //onClick={Next}
                    />
        else
            return <FormikControl
                    control="button"
                    variant="primary"
                    type="button"
                    children="Next"
                    onClick={Next}
                    />
    }
    const RenderBackButton=()=>{
        if (formStep===0)
            return undefined
        else
            return  <FormikControl
                        control="button"
                        variant="secondary"
                        type="button"
                        children="Back"
                        onClick={Back}/>        
      }

    const Next=()=>{
        setFormStep(cur=>cur+1);
    }
    const Back=()=>{
        setFormStep(cur=>cur-1);
    }
    return (
        <div className="h-screen bg-gray-100 flex flex-cols justify-center">
            <div className="max-w-md w-full mx-auto">
                <div className="mt-4 bg-white border border-gray-300 max-w-md w-full mx-auto p-8">
                <Formik
                initialValues={initialValues}
                validationSchema={validationSchema}
                onSubmit={onSubmit}
                >
                {
                    formik=>{
                        return <Form>
                             {formStep===0 && <section>
                                <div className="text-center font-medium text-3xl font-bold text-yellow-300 mt-2">
                                        Resident Personal Details
                                  </div>
                                <div className="grid grid-cols-2">
                        <FormikControl
                            control="input"
                            type="text"
                            label="Enter Your First Name"
                            placeholder="Enter Your First Name"
                            name="firstName"
                            />
                            <FormikControl
                            control="input"
                            type="text"
                            label="Enter Your Last Name"
                            placeholder="Enter Your Last Name"
                            name="lastName"
                            />
                        </div>
                        <FormikControl
                            control="input"
                            type="email"
                            label="Enter Your Email Id"
                            placeholder="Enter Your Email Id"
                            name="emailId"
                            />
                            <FormikControl
                            control="input"
                            type="tel"
                            label="Enter Your Phone Number"
                            placeholder="Enter Your Phone Number"
                            name="phoneNo"
                            />
                            </section>
                            }
                             <section>
                                <FormikControl
                                control="select"
                                label="Enter Your Wing"
                                options={WingOptions}/>
                                </section>
                            <div className="flex flex-cols justify-center">
                                {RenderNextButton()}
                                 {RenderBackButton()}
                            </div>
                        </Form>
                    }
                        
                }
            </Formik>
                </div>
            </div>
        </div>
    )
}

export default ResidentDetailsForm
