import React from 'react'

function Sidebar() {
    const [showMenu,setShowMenu]=React.useState(true);
    const toggle=()=>{
        setShowMenu(!showMenu);
        console.log(showMenu)
    }
    return (
        <div>
             <nav className={showMenu?"bg-gray-500 w-40 h-screen z-50 relative":"hidden"}>
             <div className="block">
               <div>
               <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                   <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" /></svg>
               </div>
             </div>
         </nav>
         <button className="focus:outline-none focus:ring-0 bottom-0 right-0 fixed py-4 px-4 rounded " onClick={toggle}>
                  { showMenu?<svg className="w-6 h-6 transition duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>
                      :
                      <svg className="w-6 h-6 transition duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" /></svg>}
                   </button>
        </div>
        
    )
}

export default Sidebar
