import * as React from "react";
import {useFormik} from "formik";
import * as Yup from "yup";

const initialValues ={
    firstName:"",
    secondName:"",
    email:"",
    contact:"",
    buildingCode:"",
    password:""
}
const onSubmit =values=>{
    console.log('formik values',values)
}
const validate =values =>{
    let error ={}
    if (!values.firstName){
        error.firstName="Required First Name"
    }
    if (!values.secondName){
        error.secondName="Required Last Name"
    }
    if (!values.email){
        error.email="Required Email ID"
    }else if (!/^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$/.test(values.email)){
        error.email="Invalid Email id"
    }
    if (!values.contact){
        error.contact="Required Contact no"
    }
    if (!values.buildingCode){
        error.buildingCode="Required Building Code"
    }
    if (!values.password){
        error.password="Password Required"
    }

    return error
}

const validateSchema = Yup.object({
    firstName:Yup.string().required("First Name Required")
})
function UserSignup(){

    const formik =useFormik({
        initialValues,
        onSubmit,
        validate
    })
console.log("visited",formik.touched);
    return(
       <div className="h-screen">
        <div className="mt-10 flex  justify-center items-center  sm:mx-auto sm:max-w-md sm:w-full py-6 px-4 shadow rounded-xl sm:px-6">
    <form className="mb-6 space-y-6 grid justify-center" onSubmit={formik.handleSubmit} >
    <div className="grid grid-cols-2 ">
    <label className="block text-base font-medium text-gray-700 uppercase mb-2" htmlFor="firstName">Enter First Name</label>
    <label className="block text-base font-medium text-gray-700 uppercase mb-2" htmlFor="secondName">Enter Second Name</label>
    <input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300 space-x-4"
     type="text"
      id="firstname"
       name="firstName"
       placeholder="Enter First Name"
       onChange={formik.handleChange}
       onBlur={formik.handleBlur}
       value={formik.values.firstName}
       />    
    <input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300"
     type="text"
      id="secondname"
       placeholder="Enter Second Name" 
       name="secondName"
       onChange={formik.handleChange}
       onBlur={formik.handleBlur}
       value={formik.values.secondName}
       />      
       {formik.touched.firstName && formik.errors.firstName ? <div className="text-red-400">{formik.errors.firstName}</div> :null}  
       {formik.touched.secondName && formik.errors.secondName ? <div className="text-red-400">{formik.errors.secondName}</div> :null}  
    </div>
    <div className="">
    <label className="block text-base font-medium text-gray-700 mb-2 uppercase" htmlFor="Email">Enter Email id</label>
    <input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300" 
    type="email" 
    id="email" 
    placeholder="Enter Email id" 
    name="email"
    onChange={formik.handleChange}
    onBlur={formik.handleBlur}
    value={formik.values.email}
    />        
     {formik.touched.email && formik.errors.email ? <div className="text-red-400">{formik.errors.email}</div> :null} 
    </div>
    <div className="">
    <label className="block text-base font-medium text-gray-700 mb-2 uppercase" htmlFor="conatctNo">Enter contact no.</label>
    <input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300"
    type="tel" 
    id="contactno"
     placeholder="Enter contact no." 
     name="contact"
     onChange={formik.handleChange}
     onBlur={formik.handleBlur}
     value={formik.values.contact}
     />     
      {formik.touched.contact && formik.errors.contact ? <div className="text-red-400">{formik.errors.contact}</div> :null}    
    </div>
    <div className="">
    <label className="block text-base font-medium text-gray-700 mb-2 uppercase" htmlFor="Email">Enter Building Code </label>
    <input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300" 
    type="text" 
    id="buildingCode" 
    placeholder="Enter Building Code" 
    name="buildingCode"
    onChange={formik.handleChange}
    onBlur={formik.handleBlur}
    value={formik.values.buildingCode}
    />        
     {formik.touched.buildingCode && formik.errors.buildingCode ? <div className="text-red-400">{formik.errors.buildingCode}</div> :null} 
    </div>
    
    <div className="">
<label className="block text-base font-medium text-gray-700 mb-2" htmlFor="Password">Password</label>
<input className="w-full border border-gray-300 px-3 py-2 rounded-lg shadow-sm focus:outline-none focus:border-gray-300 focus:ring-2 focus:ring-gray-300" 
type="password"
 id="password" 
 placeholder="Enter password"
  name="password"
  onChange={formik.handleChange}
  onBlur={formik.handleBlur}
  value={formik.values.password}
  />
   {formik.touched.password && formik.errors.password ? <div className="text-red-400">{formik.errors.password}</div> :null} 
</div>
<div className="flex items-center justify-center">
    <button className="py-2 px-4 border rounded bg-yellow-500 text-white focus:outline-none focus:ring-2 focus:ring-yellow-300 hover:bg-yellow-700 focus:ring-offset-2" type="submit">signup</button>
    <button className="py-2 px-4 border rounded bg-gray-500 text-white focus:outline-none focus:ring-2 focus:ring-gray-300 hover:bg-gray-700 focus:ring-offset-2" type="reset">Reset</button>
</div>
</form>
</div>
       </div>
    )
    }
    export default UserSignup;
